create database data;

create table SanPham (
	Masp char(100) primary key,
	Tensp Nvarchar(255) not null,
	LoaiSp Nvarchar(255) not null,
	ThuongHieu varchar(255) not null,
	Gia decimal(12,4) check (Gia>=0),
	SoLuong int check (Soluong>=0),
	TrangThai Nvarchar(255),
);
create table HinhAnh (
	SanPhamMasp char(100) foreign key references SanPham(Masp),
	Hinh1 varchar(255),
	Hinh2 varchar(255),
	Hinh3 varchar(255),
	Hinh4 varchar(255),
	Hinh5 varchar(255),
	Hinh6 varchar(255),
);

create table MoTa (
	SanPhamMaSP char(100) foreign key references SanPham (Masp),
	TieuDe Nvarchar(255),
	Mota Ntext,
	HinhAnh varchar(255),
);

create table ThongSo (
	SanPhamMasp char(100) foreign key references SanPham(Masp),
	ThongSo Nvarchar(255) not null,
	GiaTriTS Nvarchar(255) not null,
);


create table GioHang (
	ID int primary key identity,
	SanPhamMaSP char(100) unique foreign key references SanPham(Masp),
	SoLuong int check (SoLuong >= 0),
);

create table LikeSP (
	ID int primary key identity,
	SanPhamMaSP char(100) unique foreign key references SanPham (Masp),
);

create table DaDat (
	ID int identity primary key,
	TenKH Nvarchar(255) not null,
	SoDt int not null,
	DiaChi Nvarchar(255),
	SanPhamMaSP char(100) foreign key references SanPham(Masp),
	SoLuong int,
	GhiChu Ntext,
);
create table BinhLuan (
	ID int primary key identity,
	SanPhamMaSP char(100) foreign key references SanPham(Masp),
	HoTen Nvarchar(255) not null,
	Email varchar(255),
	NoiDung Ntext not null,
);
create table SubBinhLuan (
	ID int primary key identity,
	BinhLuanId int foreign key references BinhLuan(ID),
	HoTen Nvarchar(255) not null,
	Email varchar(255),
	NoiDung Ntext not null,
);

