	VALUES('00033291',N'',N'Đi cùng dung tích bình chứa là 20 lít, đi cùng là công suất 2500W thì bình tắm nóng lạnh này là một sự lựa chọn lý tưởng cho gia đình ít người (dưới 3 thành viên).','https://hc.com.vn/i/ecommerce/media/ckeditor_2584319.jpg'),
		('00033291',N'Bình tắm nóng lạnh mang đến nhiều công dụng cho người sử dụng',N'Với chiếc bình tắm nóng lạnh Ariston có thể chia thành nhiều đường dẫn nước, bạn có thể sử dụng được nước nóng cho rất nhiều sinh hoạt khác nhau và tùy chỉnh nhiệt độ nước phù hợp với từng nhu cầu sử dụng: tắm bồn, tắm vòi sen, rửa mặt hoặc rửa rau, củ, trái cây....','https://hc.com.vn/i/ecommerce/media/ckeditor_2584313.jpg'),
		('00033291',N'Cầu dao chống rò điện ELCB',N'Nhờ có cầu dao chống giật điện ELCB thông minh giúp máy tự động ngắt nguồn nếu phát hiện ra sự cố rò rỉ, hạn chế sự cố chạm điện bên trong máy, bảo vệ an toàn tuyệt đối cho cả gia đình bạn và người thân khỏi nguy cơ bị điện giật.','https://hc.com.vn/i/ecommerce/media/ckeditor_2584315.jpg'),
		('00033291',N'Hệ thống tạo Ion Bạc khử trùng, mang lại nguồn nước nóng sạch khuẩn',N'Bình nóng lạnh gián tiếp Ariston được trang bị công nghệ Ion Bạc diệt khuẩn, để lọc nước. Nhờ vậy bình sẽ giúp ngăn chặn sự sinh sôi, phát triển của vi khuẩn gây hại hay dị ứng, mang lại cho bạn nguồn nước nóng sạch khuẩn và an toàn.','https://hc.com.vn/i/ecommerce/media/ckeditor_2584316.jpg'),
		('00033291',N'Lòng bình được tráng lớp men TITAN ngăn chặn nước rò rỉ',N'Titan là chất liệu rất bền, chịu nhiệt độ cao, chống ăn mòn tốt. Vì thế, sản phẩm bình nóng lạnh này có thể chống rò rỉ nước từ bên trong cực tốt, tăng cường tuổi thọ cho máy.','https://hc.com.vn/i/ecommerce/media/ckeditor_2584314.jpg'),
		('00032087',N'Kiểu dáng nhỏ gọn, trang nhã',N'Bình nóng lạnh gián tiếp Ariston AN2 30 R 2.5 FE – MT mang sắc màu trang nhã, đi cùng thiết kế hình hộp vuông nhỏ gọn, phù hợp mọi kiểu không gian và không chiếm nhiều diện tích của phòng tắm.','https://hc.com.vn/i/ecommerce/media/ckeditor_2718767.jpg'),
		('00032087',N'Bình chứa dung tích 30 lít',N'Bình nóng lạnh Ariston AN2 30 R 2.5 FE – MT có bình chứa với dung tích 30 lít, là lựa chọn lý tưởng cho gia đình dưới 5 thành viên.','https://hc.com.vn/i/ecommerce/media/ckeditor_2718768.jpg'),
		('00032087',N'Cầu dao chống rò điện ELCB',N'Khi phát hiện sự cố rò rỉ điện, cầu dao ELCB của bình nóng lạnh Ariston sẽ tự động ngắt mạch điện hoạt động, bảo vệ an toàn cho bạn và gia đình. Kết hợp với lớp vỏ ngoài chống thấm nước IPX1 bảo vệ cho các linh kiện bên trong khỏi nguy cơ bị chập mạch, cháy nổ do nước thấm, giúp bình nóng lạnh luôn bền đẹp.','https://hc.com.vn/i/ecommerce/media/ckeditor_2718770.jpg'),
		('00032087',N'Bộ ổn định nhiệt TBST',N'Bình nóng lạnh AN2 30 R 2.5 FE – MT sẽ tự động ngắt chức năng làm nóng khi nhiệt độ nước vượt quá mức cho phép, bảo vệ an toàn cho làn da của bạn. Theo đó, bạn sẽ không còn lo lắng vì nước quá nóng gây bỏng rát cho làn da, đặc biệt là da trẻ nhỏ.',''),
		('00032087',N'Lòng bình tráng men titan bền đẹp',N'Ariston AN2 30 R 2.5 FE – MT thiết kế lòng bình tráng men titan bền đẹp, ngăn chặn tối ưu tình trạng rò rỉ nước từ bên trong','https://hc.com.vn/i/ecommerce/media/ckeditor_2718771.jpg'),
		('GD.001462',N'Thiết kế đơn giản, hiện đại',N'Bình nóng lạnh gián tiếp Ariston SL2 30 R 2.5 FE MT sở hữu thiết kế dạng chữ nhật ngang đơn giản nhưng không kém phần tinh tế, mang đến sự hài hòa cho không gian nội thất. Đi cùng công suất 2500W và dung tích bình chứa lên đến 30 lít, bình tắm nóng lạnh này là lựa chọn phù hợp cho những gia đình 3 - 5 người hoặc ít thành viên hơn nhưng có nhu cầu sử dụng nhiều nước nóng.','https://hc.com.vn/i/ecommerce/media/ckeditor_2718775.jpg'),
		('GD.001462',N'Cầu dao chống rò điện ELCB',N'Khi phát hiện sự cố rò rỉ điện, cầu dao ELCB của bình nóng lạnh Ariston sẽ tự động ngắt mạch điện hoạt động, bảo vệ an toàn cho bạn và gia đình.','https://hc.com.vn/i/ecommerce/media/ckeditor_2718777.jpg'),
		('GD.001462',N'Bộ ổn định nhiệt TBST',N'Bình nóng lạnh Ariston SL2 30 R 2.5 FE MT sẽ tự động ngắt chức năng làm nóng khi nhiệt độ nước vượt quá mức cho phép, bảo vệ an toàn cho làn da của bạn. Theo đó, bạn sẽ không còn lo lắng vì nước quá nóng gây bỏng rát cho làn da, đặc biệt là da trẻ nhỏ.','https://hc.com.vn/i/ecommerce/media/ckeditor_2718779.jpg'),
		('GD.001462',N'Vỏ chống thấm nước',N'Ariston SL2 30 R 2.5 FE MT có vỏ ngoài chống thấm nước bảo vệ cho các linh kiện bên trong khỏi nguy cơ bị chập mạch, cháy nổ do nước thấm, giúp bình nóng lạnh luôn bền đẹp ngay cả trong môi trường có độ ẩm cao.','https://hc.com.vn/i/ecommerce/media/ckeditor_2718781.jpg'),
		('GD.001462',N'Hệ thống tạo Ion Bạc kháng khuẩn làm sạch nước',N'Bình nóng lạnh gián tiếp Ariston được trang bị công nghệ Ion bạc diệt khuẩn giúp ngăn chặn sự sinh sôi, phát triển của vi khuẩn gây hại hay dị ứng, mang lại cho bạn nguồn nước nóng sạch khuẩn và an toàn.','https://hc.com.vn/i/ecommerce/media/ckeditor_2910000.jpg'),
		('GD.001462',N'Lòng bình tráng men titan bền đẹp',N'Bình nóng lạnh gián tiếp Ariston SL2 30 R 2.5 FE MT  thiết kế lòng bình tráng men titan bền đẹp, ngăn chặn tối ưu tình trạng rò rỉ nước từ bên trong.','');

insert into MoTa(SanPhamMaSP,TieuDe,Mota,HinhAnh)
values('00022657',N'Thiết kế ngang độc đáo, trang nhã',N'Bình nóng lạnh Ferroli Verdi-TE 20 có điểm nhấn màu cam nổi bật bên góc phải đi cùng với đó là sắc trắng trang nhã tổng thể, dễ dàng kết hợp với mọi kiểu không gian phòng tắm của gia đình bạn. Dung tích 20 lít giúp bạn trữ nhiều nước nóng hơn và yên tâm thoải mái trong việc sử dụng nước nóng cho cả gia đình khoảng 2 - 3 thành viên trong 1 lần tắm.','https://hc.com.vn/i/ecommerce/media/ckeditor_2584352.jpg'),
		('00022657',N'',N'Máy nước nóng gián tiếp phù hợp với gia đình ở khu vực có khí hậu lạnh, nhất là vào mùa đông khí hậu có thể xuống dưới 20 độ C. Hoặc những gia đình có bồn tắm, hay hộ kinh doanh khách sạn nhỏ cũng rất thích hợp dùng loại máy nước nóng này.','https://hc.com.vn/i/ecommerce/media/ckeditor_2584353.jpg'),
		('00022657',N'Chống rò rỉ điện, bảo đảm an toàn với cầu dao ELCB',N'Trang bị dây nguồn chống giật ELCB, bình nước nóng Ferroli Verdi 20L TE 20 Lít có khả năng phát hiện rò rỉ điện và tự động ngắt nguồn giúp bảo vệ người dùng khỏi những tai nạn do sự cố về điện.','https://hc.com.vn/i/ecommerce/media/ckeditor_2584355.jpg'),
		('00022657',N'Bình chứa tráng men Titanium chống rò rỉ nước từ bên trong',N'Bình chứa của bình nước nóng Ferroli VERDI 20L TE 20 Lít được trang bị lớp men Titan đặc biệt phủ kín bề mặt bên trong bình bằng công nghệ tĩnh điện tiên tiến nhất hiện nay. Sau đó lớp men này sẽ được nung nóng ở 85 độ C, làm cho bề mặt bên trong bình thành một mặt phẳng thống nhất, giúp chống rò rỉ điện và đảm bảo chất lượng nước trong bình nước nóng.','https://hc.com.vn/i/ecommerce/media/ckeditor_2584356.jpg');

