insert into MoTa(SanPhamMaSP,TieuDe,Mota,HinhAnh)
values('00022664',N'Lớp cách nhiệt không chứa CFC',N'Quá trình làm nóng nước sẽ luôn diễn ra nhanh hơn và nước nóng trong bình luôn được bảo quản ở nhiệt độ tốt nhất, kể cả lượng nước chưa sử dụng hết hết, tránh gây lãng phí năng lượng tiêu thụ cũng như khoảng thời gian làm nóng nước. Lớp cách nhiệt có tác dụng đảm bảo tránh được sự thất thoát nhiệt và tiêu hao nhiều điện năng. Lớp cách nhiệt được cấu tạo bằng lớp bột nhựa tổng hợp đậm đặc không có chất hại CFC.','https://hc.com.vn/i/ecommerce/media/ckeditor_2585080.png'),
('00022664',N'Van xả cặn tiện lợi',N'Việc vệ sinh máy sẽ trở nên dễ dàng hơn, người tiêu dùng có thể tự tháo van, xả cặn, sục rửa bình mà không cần phải gọi dịch vụ.','https://hc.com.vn/i/ecommerce/media/ckeditor_2585081.jpg'),
('00022664',N'Rơ le chống cháy khô',N'Để tránh hiện tượng cháy nổ, máy nước của Ferroli được thiết kế rơ le chống cháy khô, khi có hiện tượng xấu xảy ra, rơ le sẽ tự động nhảy, đảm bảo an toàn cao nhất cho người sử dụng. Khi thanh đốt chưa ngập trong nước mà đã được làm nóng, thanh đốt có nguy cơ bị cháy và truyền đến các bộ phận khác trong máy, thậm chí là cháy nguyên máy, gây nguy hại cho người dùng.',''),
('00022664',N'Lớp phủ men xanh bền đẹp',N'Được sản xuất bằng công nghệ sơn mạ tĩnh điện tiên tiến nhất và được đem vào nung trong lò nung ở mức nhiệt 8700 độ C, lớp tráng men có độ dày 250 microns, vì vậy sản phẩm có chất lượng, tuổi thọ cao.',''),
('00022664',N'Bình chứa tráng TiTan siêu bền',N'Bình chứa của Ferroli đã được cải tiến trên công nghệ tráng men Titan nhờ đó giúp đảm bảo độ bền cao, chống va đập, chống sốc.',''),
('00022664',N'Thanh nhiệt tráng men bền bỉ',N'Là thế hệ mới nhất sử dụng bức xạ nhiệt, được sản xuất trên công nghệ PLUS từ trung tâm nghiên cứu và phát triển toàn cầu Ferroli tại San Bonifacio. Công nghệ PLUS đã tạo ra sản phẩm thanh nhiệt chống bám cặn, hiệu suất làm việc cao và tiết kiệm tối đa điện năng.','https://hc.com.vn/i/ecommerce/media/ckeditor_2585083.jpg');

insert into MoTa(SanPhamMaSP,TieuDe,Mota,HinhAnh)
values('00032994',N'Thiết kế sang trọng, tiết kiệm diện tích',N'Bình nóng lạnh trực tiếp Ferroli RITA FS-4.5 TM có thiết kế màu trắng sang trọng cùng kích thước vô cùng nhỏ gọn, sẽ không chiếm nhiều diện tích không gian nhà tắm hay căn bếp của bạn.','https://hc.com.vn/i/ecommerce/media/00032994_FEATURE_57797.jpg'),
('00032994',N'An toàn tuyệt đối khi sử dụng',N'Bình nóng lạnh trực tiếp Ferroli RITA FS-4.5 TM sử dụng cơ chế làm nóng nước bằng dung môi nóng hiện đại, không phải thông qua thanh đun nhiệt bằng điện thông thường nên bạn có thể hoàn toàn yên tâm về hiện tượng rò rỉ điện trong quá trình sử dụng.',''),
('00032994',N'Nhiều chế độ bảo vệ',N'Ngoài chế độ làm nóng tách điện vô cùng an toàn, Ferroli Rita FS-4.5TM còn trang bị nhiều chức năng bảo vệ khác như: chế độ phát hiện lỗi trong lúc sử dụng, chế độ chống quá nhiệt, chống cháy khô khi lượng nước trong bình quá thấp, bộ lọc nâng cao chất lượng nước và kéo dài tuổi thọ cho thiết bị.','https://hc.com.vn/i/ecommerce/media/ckeditor_2585264.jpg'),
('00032994',N'Thay đổi công suất linh hoạt',N'Bình nóng lạnh trực tiếp Ferroli RITA FS-4.5 TM cho phép bạn điều chỉnh 3 mức độ công suất sử dụng phù hợp tùy theo nhiệt độ môi trường bên ngoài, đảm bảo bạn luôn có dòng nước với nhiệt độ thích hợp để sử dụng mà vẫn tiết kiệm điện tối đa.',''),
('00032994',N'Có thể dùng trong nhà bếp',N'Đáp ứng nhu cầu sử dụng nguồn nước nóng đa dạng ngày nay, máy nước nóng Rita được thiết kế để bạn có thể dễ dàng kết nối với các thiết bị sen vòi, vòi rửa… trong phòng tắm lẫn nhà bếp. Bạn không những có thể sử dụng nước nóng để tắm mà còn có thể dùng để rửa bát, nấu nướng, lau dọn nhà bếp…','');

insert into MoTa(SanPhamMaSP,TieuDe,Mota,HinhAnh)
values('00032391',N'Thiết kế đơn giản, thanh lịch',N'Bình nóng lạnh Kangaroo 22 lít KG68A2 có kiểu dáng ngang, thon gọn phù hợp với nhiều không gian phòng tắm khác nhau, đặc biệt là những phòng có trần thấp. Thiết kế trang nhã, nhẹ nhàng, đẹp mắt. Vỏ máy được sơn màu trắng phối vàng đồng tinh tế, lớp sơn sáng bóng ít bám bẩn và dễ dàng lau chùi giữ cho máy bền đẹp trong suốt thời gian sử dụng. Dung tích 22 lít đáp ứng nhu cầu sử dụng của gia đình 3-4 thành viên.','https://hc.com.vn/i/ecommerce/media/ckeditor_2942367.jpg'),
('00032391',N'Bình nóng lạnh gián tiếp phù hợp với khí hậu miền Bắc',N'Bình nóng lạnh Kangaroo KG68A2 hoạt động theo cơ chế làm nóng gián tiếp, phù hợp dùng cho gia đình có bồn tắm, khách sạn nhỏ hoặc khu vực có khí hậu lạnh nhất là vào mùa đông nhiệt độ xuống dưới 20 độ C.','https://hc.com.vn/i/ecommerce/media/ckeditor_2942362.jpg'),
('00032391',N'Công nghệ kháng khuẩn an toàn cho da nhạy cảm',N'Kangaroo đã ứng dụng công nghệ kháng khuẩn tiên tiến cho bình nóng lạnh bằng các phủ lên thanh nhiệt lớp Nano bạc bền bỉ, tăng độ bền của thanh nhiệt lên gấp 50 lần. Đồng thời, các phân tử bạc sẽ phát huy khả năng diệt khuẩn mạnh mẽ, ngăn chặn sự sinh sôi, phát triển của vi khuẩn gây hại hay dị ứng, an toàn tuyệt đối cho trẻ nhỏ và người có làn da nhạy cảm. Công nghệ nano bạc tối ưu bởi nó là chất vô cơ, không gây ảnh hưởng đến thành phần của nước,','https://hc.com.vn/i/ecommerce/media/ckeditor_2942369.jpg'),
('00032391',N'Làm nóng nước nhanh chóng, không cần chờ đợi lâu',N'Kangaroo KG68A2 sử dụng thanh nhiệt mạ bạc không chỉ có tác dụng kháng khuẩn, chống bám cặn mà còn có chức năng làm nóng nước nhanh chóng lên đến 20% so với bình nóng lạnh thông thường. Thêm vào đó là công suất hoạt động mạnh mẽ lên tới 2500W cũng giúp đem đến hiệu quả làm nóng nước trong thời gian ngắn, không tốn nhiều thời gian chờ đợi nước nóng.','https://hc.com.vn/i/ecommerce/media/ckeditor_2942371.jpghttps://hc.com.vn/i/ecommerce/media/ckeditor_2942371.jpg'),
('00032391',N'Điều khiển cơ đơn giản, điều chỉnh dễ dàng',N'Bạn có thể điều chỉnh công suất của bình nóng lạnh Kangaroo KG68A2 bằng điều khiển núm vặn để cài đặt nhiệt độ theo nhu cầu (Ví dụ: mùa hè để công suất thấp, mùa đông để công suất cao) tránh lãng phí điện năng không cần thiết. Đèn LED hiển thị nhiệt độ đun nóng hiện tại của bình được bố trí ngay trên mặt trước máy, giúp người dùng dễ dàng kiểm soát quá trình hoạt động và điều chỉnh phù hợp nhu cầu của gia đình.',''),
('00032391',N'Hệ thống bảo vệ an toàn, an tâm khi sử dụng',N'Bình nóng lạnh gián tiếp Kangaroo KG68A2 được trang bị hệ thống an toàn đồng bộ CSS cùng hệ thống chống giật kép, chống đun khô thông minh, giúp ngăn chặn sự cố rò rỉ điện, sự cố trong quá trình sử dụng nhằm đảm bảo an toàn cho mọi thành viên trong gia đình. Tuy nhiên, để an toàn tuyệt đối, bạn vẫn nên ngắt bình nóng lạnh trước khi tắm.','https://hc.com.vn/i/ecommerce/media/ckeditor_2942370.jpg'),
('00032391',N'Giữ nhiệt lâu, tiết kiệm điện nhờ lớp cách nhiệt PU',N'Máy nước nóng gián tiếp Kangaroo còn sở hữu lớp cách nhiệt mật độ cao PU mang lại hiệu quả cho việc giữ nước nóng được lâu hơn, tránh thất thoát nhiệt, không phải chịu ảnh hưởng bởi nhiệt độ của môi trường bên ngoài bên ngoài. Đặc biệt vào mùa đông, bạn có thể bật nước nóng 1 lần và sử dụng nhiều lần cho việc đánh răng rửa mặt, rửa tay chân mà không cần bật liên tục, giúp tiết kiệm chi phí điện năng hiệu quả.','https://hc.com.vn/i/ecommerce/media/ckeditor_2942360.jpg');

insert into MoTa(SanPhamMaSP,TieuDe,Mota,HinhAnh)
values('GD.002349',N'Thiết kế hiện đại, thanh lịch',N'Máy lọc nước Karofi 9 cấp N-E239 kích thước nhỏ gọn, tiết kiệm diện tích, kiểu dáng hiện đại, thanh lịch thích hợp với mọi vị trí và không gian khác nhau. Thiết kế phẳng kết hợp với những đường bo cong mềm mại, tinh tế. Mặt kính cường lực tạo điểm nhấn sang trọng cho ngôi nhà của bạn.','https://hc.com.vn/i/ecommerce/media/ckeditor_2848246.jpg'),
		('GD.002349',N'Màng lọc lọc RO tiên tiến gấp đôi hiệu suất',N'Màng lọc RO 100GDP nhập khẩu trong máy lọc nước Karofi 9 cấp N-E239 cho tỉ lệ thu hồi nước tinh khiết lên đến 50%-60% (trong điều kiện thí nghiệm tiêu chuẩn). Công suất lọc 20 lít/h, gấp đôi các lõi lọc RO thông thường, đáp ứng nhu cầu nước ăn uống, sinh hoạt cho cả gia đình. Bình áp nhựa đạt chuẩn an toàn sức khỏe của Châu Âu (Phiên bản sử dụng bình áp thép được sản xuất trước 04/2020).',''),
('GD.002349',N'Hệ thống 9 lõi lọc làm sạch vượt trội',N'Bạn sẽ không còn lo lắng về chất lượng nước sử dụng hàng ngày của gia đình với nguồn nước tinh khiết được lọc qua 9 lõi lọc của máy lọc nước Karofi N-E239 loại bỏ 99.99% các chất ô nhiễm, tạp chất. Hơn thế nữa, 5 lõi lọc chức năng giúp bổ sung các khoáng chất thiết yếu cho cơ thể mà màng RO đã lọc bỏ, nhờ đó nguồn nước sau lọc không chỉ an toàn mà còn tốt cho sức khỏe, tránh sự thiếu hụt khoáng chất.','https://hc.com.vn/i/ecommerce/media/ckeditor_2848247.jpg'),
('GD.002349',N'Nước uống trực tiếp đạt Quy chuẩn Quốc gia QCVN6-1:2010/BYT',N'Với hệ thống lọc mạnh mẽ nước sau lọc được lấy từ máy lọc nước Karofi N-E239 đã được chứng nhận đạt chuẩn Quốc gia nước về nước uống trực tiếp, đảm bảo an toàn tuyệt đối cho sức khỏe người sử dụng.','https://hc.com.vn/i/ecommerce/media/ckeditor_2842545.jpg');
