insert into MoTa(SanPhamMaSP,TieuDe,Mota,HinhAnh)
values('00028804',N'',N'Máy lọc nước R.O Hydrogen Kangaroo KG100HA là  dòng sản phẩm máy lọc nước hiện đại, công nghệ tiên tiến, có bổ sung Hydrogen có lợi cho sức khỏe, tạo nguồn nước ngọt lành, an toàn cho sức khỏe. Máy lọc nước phù hợp với nhu cầu sử dụng cho gia đình, nhà hàng, văn phòng… ','https://hc.com.vn/i/ecommerce/media/ckeditor_1706167.jpg'),
('00028804',N'Sử dụng công nghệ vượt trội',N'Máy lọc nước Kangaroo KG100HA sử dụng công nghệ tạo nước Hydrogen bằng khoáng gốm.  Đây là phương pháp chia tách phân tử nước tự nhiên bằng công nghệ hạt nâng cao hiệu năng bằng cách tăng bề mặt tiếp xúc với các hạt khoáng gốm hình cầu. Nhờ đó, máy lọc nước Hydrogen Kangaroo KG 100HA có khả năng tạo ra nước kiềm Hydrogen với độ PH >7  tốt cho sức khỏe.','https://hc.com.vn/i/ecommerce/media/ckeditor_2823652.jpg'),
('00028804',N'Thiết kế hiện đại, tiện dụng ',N'Máy lọc nước Hydrogen Kangaroo KG100HA-VTU thiết kế kiểu dáng nhỏ gọn. Vỏ tủ bền đẹp, sang trọng. Máy trang bị van chống rò rỉ nước đảm bảo nước không chảy ra bên ngoài máy đảm bảo độ bền cho sản phẩm và giúp không gian xếp đặt luôn khô thoáng, sạch sẽ. ','https://hc.com.vn/i/ecommerce/media/ckeditor_1706177.jpg'),
('00033338',N'Máy lọc nước Aosmith với thiết kế nhỏ gọn, kiểu dáng hiện đại, chất liệu nhựa cao cấp sáng bóng, giữ màu, dễ lau chùi.',N'Sản phẩm có thể lắp đặt trên kệ tủ, chân kệ gỗ, kệ treo tường tiện lợi, sang trọng, đẹp mắt','https://hc.com.vn/i/ecommerce/media/ckeditor_2625957.jpg'),
('00033338',N'Hệ thống 5 lõi lọc giúp lọc sạch nước, cung cấp nguồn nước tinh khiết, sạch khuẩn, an toàn cho sức khoẻ để sử dụng',N'','https://hc.com.vn/i/ecommerce/media/ckeditor_2625958.jpg'),
('00033338',N'Trong đó, lõi lọc RO – Side Stream bản quyền Mỹ, hiệu quả lọc tăng thêm 66% lượng nước tinh khiết, giảm 56% lượng nước thải, tỷ lệ lọc thải 1:1',N'Màng lọc được kéo dài tuổi thọ trung bình hơn 1.5 lần. Sản phẩm được NSF International (Hiệp hội an toàn thực phẩm và nước uống quốc tế của Mỹ) kiểm tra và chứng nhận đạt chuẩn NSF/ANSI 58 và 42.','https://hc.com.vn/i/ecommerce/media/ckeditor_2625960.jpg'),
('00033338',N'Thiết kế đơn giản, dễ lắp đặt sử dụng. Công suất lọc 11.8 lít/giờ, dung tích bình chứa 6 lít đủ dùng cho nhu cầu sử dụng gia đình hay văn phòng, công sở...',N'Bình chứa được làm bằng thép không rỉ, có túi hơi cao su chứa nước bên trong, chuẩn vệ sinh an toàn thực phẩm đảm bảo không ảnh hưởng đến độ tinh khiết của nước. Ngăn chặn vi khuẩn xâm nhập, tránh hoàn toàn hiện tượng tái nhiễm khuẩn trong nước.','https://hc.com.vn/i/ecommerce/media/ckeditor_2625961.jpg'),
('GD.001151',N'Thiết kế tinh tế, sang trọng',N'Máy lọc nước RO Sunhouse SHA8858K nổi bật với hoa văn hiện đại, thiết kế kính cường lực sáng bóng, tràn viền. Kích thước vừa phải giúp bạn dễ dàng đặt máy ở nhiều vị trí khác nhau như phòng bếp, phòng khách hay phòng ngủ mà vẫn đảm bảo tính sang trọng cho không gian căn nhà của bạn. Vỏ tủ tôn sơn dày cực chắc chắn,','https://hc.com.vn/i/ecommerce/media/ckeditor_2743501.jpeg'),
('GD.001151',N'Hiệu quả lọc vượt trội với hệ thống 8 lõi lọc',N'Nhờ trang bị tới 8 lõi lọc, mỗi lõi có 1 công năng riêng biệt nên máy lọc nước R.O Sunhouse SHA8858K có khả năng loại bỏ các tạp chất kim loại nặng, độc tố gây ung thư, bệnh đường tiêu hoá, viêm dây thần kinh, thoái hoá cột sống đau lưng,... Loại bỏ vi khuẩn có kích thước lớn hơn ≥ 1µm, bẩn bùn và đất rỉ sét có kích thước ≥ 5µm. Đặc biệt.','https://hc.com.vn/i/ecommerce/media/ckeditor_2743502.jpeg'),
('GD.001151',N'Đạt chuẩn kĩ thuật quốc gia về nước uống trực tiếp QCVN 6-1:2010/BYT do Bộ Y Tế cấp.',N'','');

insert into MoTa(SanPhamMaSP,TieuDe,Mota,HinhAnh)
values('GD.001151',N'Hệ thống bơm hút 2 chiều',N'Máy lọc nước Sunhouse còn có hệ thống bơm hút 2 chiều phù hợp với gia đình có bể chứa nước âm 2 - 3 mét hoặc nhà cao tầng.',''),
('GD.001151',N'Công suất lọc mạnh mẽ',N'Công suất lọc của máy lên đến 15 lít/h, đáp ứng nhu cầu sử dụng nước trong nấu nướng, nước uống cho gia đình 4-5 người hay các văn phòng nhỏ chỉ có nhu cầu về nước uống. Dung tích bình chứa 8 lít giúp người dùng có một lượng nước nhất định để sử dụng ngay mà không phải chờ đợi lâu.',''),
('GD.001151',N'Cấu tạo chắc chắn',N'Các cút nối của máy lọc nước SHA8858K được làm bằng nhựa gia cường, tuổi thọ cao, chịu lực vặn tốt. Zen cút được sản xuất đồng bộ, không có chi tiết thừa, giảm tối đa tỉ lệ rò rỉ nước. Đặc biệt, cút nối nhanh của sản phẩm giúp tiết kiệm thời gian lắp đặt, chỉ còn 15 phút so với 45 phút như trước đây do sản phẩm đã được đấu nối sẵn tới 90%.',''),
('GD.001151',N'Trang bị tính năng hiện đại thông minh',N'Bên cạnh chức năng lọc nước, máy lọc nước R.O SUNHOUSE SHA8858K 8 lõi còn được trang bị nhiều tính năng tự động hiện đại như: ngừng hoạt động khi áp lực nước đầu vào quá thấp, tự động ngắt khi nước đầy bình và tự động xả nước thải.','https://hc.com.vn/i/ecommerce/media/ckeditor_2743500.jpeg'),
('GD.001142',N'Kiểu dáng hiện đại, sang trọng',N'Máy lọc nước RO Hydrogen FujiE RO-990CAB kiểu dáng tủ đứng hiện đại, sang trọng với gam màu đen nổi bật. Bạn có thể lắp đặt tủ ở nhiều vị trí trong nhà như phòng bếp, phòng khách hay phòng ngủ mà vẫn đảm bảo tính thẩm mỹ cho không gian sống của gia đình. Tủ cường lực in công nghệ 3D, sử dụng mặt kính cường lực Temper cao cấp bền đẹp, chân đế tủ làm bằng nhựa ABS có thể uốn dẻo và không vỡ.','https://hc.com.vn/i/ecommerce/media/ckeditor_2744743.jpg'),
('GD.001142',N'Hệ thống lọc 9 cấp đảm bảo tiêu chuẩn nước uống trực tiếp',N'Nước sau lọc từ máy lọc nước RO FujiE đã được kiểm nghiệm bởi Viện PASTEUR TP HCM và Tổng cục đo lường chất lượng công bố nguồn nước đạt chuẩn.','https://hc.com.vn/i/ecommerce/media/ckeditor_2744748.jpg'),
('GD.001142',N'Cấu tạo thông minh, tiện lợi',N'Hệ thống cút nối thông minh giúp giảm tối đa rò rỉ nước, giảm thời gian lắp đặt và cũng dễ dàng thay lõi. Điểm đặc biệt nữa là trên mặt tủ được thiết kế khay chứa nước, giúp nước không bị tràn ra bên ngoài.','https://hc.com.vn/i/ecommerce/media/ckeditor_2744744.jpg'),
('GD.001142',N'Vòi gạt lấy nước được làm bằng Inox sáng bóng bền bỉ, chất liệu an toàn cho người sử dụng',N'','https://hc.com.vn/i/ecommerce/media/ckeditor_2744745.jpg'),
('GD.001142',N'Hệ thống tự động kiểm soát an toàn vượt trội',N'Máy lọc nước RO FujiE RO-990CAB được trang bị những tính năng tiện ích như: Tự động ngừng hoạt động khi áp lực đầu nguồn không đủ, Tự động ngừng hoạt động khi nước đầy bình (đủ áp trong bình áp), Tự động xả nước thải. Những tính năng này giúp máy hoạt động bền bỉ, tiết kiệm điện hơn.',''),
('GD.004403',N'Thiết kế đơn giản, trang nhã',N'Máy lọc nước RO Coway P-300L sở hữu thiết kế đơn giản, trang nhã cùng tông màu trắng phối xanh nhẹ nhàng, tinh tế, tạo cảm giác dịu mát, góp phần làm đẹp cho ngôi nhà của bạn. Máy có kiểu dáng để bàn nhỏ gọn, dễ dàng di chuyển, lắp đặt ở nhiều vị trí khác nhau giúp tiết kiệm diện tích, tối ưu không gian cho gia đình nhỏ','https://hc.com.vn/i/ecommerce/media/ckeditor_3147243.png'),
('GD.004403',N'Tiết kiệm chi phí với chế độ lọc không cần nguồn điện',N'Máy lọc nước RO Coway P-300L 5 lõi có thể hoạt động mà không cần đến nguồn điện, bạn sẽ không còn phải băn khoăn về chi phí tiền điện hàng tháng hay lo lắng về an toàn cháy nổ.','https://hc.com.vn/i/ecommerce/media/ckeditor_3147247.jpg'),
('GD.004403',N'Hệ thống lọc 5 lõi cho nguồn nước uống trực tiếp an toàn',N'Máy lọc nước Coway P-300L sử dụng công nghệ lọc RO thẩm thấu ngược, lọc sạch những vi khuẩn nhỏ đến 0,001 micromet. Hệ thống lọc 5 lõi tăng cường hiệu quả của màng lọc RO, cung cấp cho bạn nguồn nước tinh khiết, ngọt mát. Nước sau lọc có thể uống trực tiếp mà không cần đun sôi, tiết kiệm điện năng cho người sử dụng.','https://hc.com.vn/i/ecommerce/media/ckeditor_3147242.png'),
('GD.004403',N'Dễ dàng lấy nước liên tục với vòi nhấn tiện lợi',N'Coway P-300L trang bị vòi lấy nước dạng nhấn thao tác nhẹ nhàng, dễ sử dụng. Lấy nước chảy liên tục với thao tác gạt cần lên trên giúp bạn rảnh tay hơn. Đầu vòi nước còn có thể tháo rời để vệ sinh, đảm bảo nguồn nước bạn sử dụng luôn sạch sẽ.','https://hc.com.vn/i/ecommerce/media/ckeditor_3147244.jpg'),
('GD.004403',N'Bình chứa dung tích lớn',N'Bên cạnh các tính năng ưu việt, máy lọc nước trang bị bình chứa dung tích 7.6 lít một dung tích khá lớn cho một chiếc máy nhỏ gọn, giúp lưu trữ và cung cấp nguồn nước sạch liên tục đủ cho cả gia đình. Công suất lọc 5 lít/h Nước sau khi lọc được lưu trữ trong bình chứa kín, không lo sợ tình trạng nước bị tái nhiễm khuẩn.','https://hc.com.vn/i/ecommerce/media/ckeditor_3147248.png'),
('GD.004403',N'Ngăn tràn nước nhờ hệ thống kiểm soát mực nước kép',N'Máy lọc nước Coway P-300L được trang bị hệ thống kiểm soát mực nước kép với phao nổi điều chỉnh tự động mức nước ngăn nước tràn ra ngoài.',''),
('00032063',N'Thiết kế sang trọng',N'Nồi cơm điện tử cao tần Sharp 1,8 lít KS-IH190V-GL được thiết kế với kiểu dáng đơn giản, cùng màu sắc bắt mắt sẽ góp phần không nhỏ giúp không gian bếp của gia đình bạn đẹp và sang trọng hơn.','https://hc.com.vn/i/ecommerce/media/ckeditor_1969126.jpg'),
('00032063',N'Dung tích 1,8 lít',N'Nồi cơm điện dung tích 1.8 lít phục vụ tốt cho các gia đình 4 – 6 người ăn','https://hc.com.vn/i/ecommerce/media/ckeditor_1969127.jpg'),
('00032063',N'Bảng điều khiển cảm ứng hiện đại',N'Bảng điều khiển cảm ứng hiện đại, dễ hiểu, dễ dùng, điều khiển đa chức năng nấu của nồi từ nấu cơm, nấu cháo, nấu cơm trộn, nấu cơm nồi đất, nấu súp, hấp, làm bánh.','https://hc.com.vn/i/ecommerce/media/ckeditor_1969128.jpg'),
('00032063',N'Chế độ hẹn giờ tiện lợi',N'Nồi có hẹn giờ nấu xong tiện dụng, hỗ trợ người dùng nấu ăn ngay cả những lúc bận rộn.',''),
('00032063',N'Lòng nồi chống dính ',N'Lòng nồi cơm điện cao tần bằng nhôm dập dày bền, chống móp méo, giữ nhiệt hiệu quả, có phủ men chống dính nấu ăn tiện lợi.','https://hc.com.vn/i/ecommerce/media/ckeditor_1969130.jpg'),
('00032063',N'Công nghệ nấu cao tần điều chỉnh chính xác nhiệt độ ',N'Nồi nấu cơm bằng công nghệ nấu cao tần điều chỉnh chính xác nhiệt độ nấu cho từng món nấu. Nồi không dùng mâm nhiệt mà dùng cảm ứng từ gia nhiệt đều khắp nồi nấu, nhờ đó món nấu chín ngon và bảo toàn dinh dưỡng tốt nhất.','https://hc.com.vn/i/ecommerce/media/ckeditor_1969129.jpg'),
('00032063',N'Van thoát hơi thông minh',N'Van thoát hơi thông minh giữ hơi nước ổn định trong quá trình nấu, bảo toàn vitamin sẵn có trong gạo, cho cơm ngon dinh dưỡng trọn vẹn.','https://hc.com.vn/i/ecommerce/media/ckeditor_1969131.jpg');

