INSERT INTO MoTa 
	VALUES('00029913',N'Thiết kế nhỏ gọn, di chuyển linh hoạt',N'Thiết kế nhỏ gọn cùng dung tích sử dụng 85 lít, tủ lạnh Electrolux EUM09000SA là sản phẩm phù hợp với các bạn sinh viên hoặc gia đình 1-2 thành viên, các phòng kinh doanh dịch vụ lưu trú như khách sạn có không gian phòng không quá rộng. Cửa tủ bằng thép không gỉ sáng bóng, sang trọng đảm bảo tính thẩm mỹ cho không gian sống của bạn. Hơn thế nữa, chất liệu thép không gỉ còn tăng độ bền cho tủ. Sản phẩm có kích thước nhỏ nên dễ dàng di chuyển và đặt được ở các vị trí thích hợp trong căn nhà của bạn mà không tốn nhiều diện tích.','https://hc.com.vn/i/ecommerce/media/ckeditor_2721499.jpg'),
		('00029913',N'Tiết kiệm chi phí điện năng',N'Tủ lạnh Electrolux 85 lít EUM0900SA chỉ tiêu thụ 0.55 kW/ngày (203.56KWH/năm) giúp bạn tiết kiệm chi phí điện hàng tháng, đặc biệt với những người ở trọ, giá điện cao.','https://hc.com.vn/i/ecommerce/media/ckeditor_2721503.jpg'),
		('DL.002009',N'Thiết kế nhỏ gọn, di chuyển dễ dàng',N'Tủ lạnh Aqua AQR-D99FA BS thuộc dòng tủ lạnh mini 1 cửa, tủ có thiết kế đẹp mắt, vỏ thép chắc chắn, dễ vệ sinh. Tủ lạnh Aqua dung tích 90 lít phù hợp với những không gian nhỏ hẹp, nhu cầu lưu trữ ít như các bạn sinh viên, người đi làm ở trọ hay những gia đình có nhu cầu sử dụng các loại đồ uống, hoa quả đặt trong phòng khách, phòng ngủ. Với kích thước nhỏ gọn, người dùng cũng dễ dàng di chuyển tủ khi có nhu cầu thay đổi chỗ để.','https://hc.com.vn/i/ecommerce/media/ckeditor_2811290.jpg'),
		('DL.002009',N'Hệ thống làm lạnh trực tiếp làm lạnh nhanh',N'Tủ lạnh mini Aqua này sử dụng công nghệ làm lạnh trực tiếp. Công nghệ này hoạt động dựa trên nguyên tắc hơi lạnh được thổi trực tiếp đến nhiều vị trí khác nhau trong tủ, đảm bảo làm lạnh nhanh, vận hành êm, giữ thực phẩm luôn tươi ngon. Đặc biệt, tủ lạnh Aqua AQR-D99FA BS sử dụng điện rất tiết kiệm, điều này rất quan trọng với những người ở trọ khi giá điện cao.','https://hc.com.vn/i/ecommerce/media/ckeditor_2811285.jpg'),
		('DL.002006',N'Thiết kế nhỏ gọn, gam màu đen sang trọng',N'Tủ lạnh Aqua 50 lít AQR AQR-D59FA BS có 1 cửa mở tiện lợi cho việc lấy đồ. Thiết kế nhỏ gọn, đơn giản nhưng không kém phần sang trọng với gam màu đen thanh lịch, phù hợp lắp đặt ở mọi không gian, đặc biệt là những nơi chật hẹp. Tủ lạnh mini Aqua 50 lít đáp ứng nhu cầu lưu trữ ít như các bạn sinh viên ở trọ hay những gia đình, khách sạn có nhu cầu sử dụng các loại đồ uống mát, bảo quản hoa quả đặt trong phòng khách, phòng ngủ. Kích thước nhỏ cho phép người dùng di chuyển thay đổi vị trí một các dễ dàng.','https://hc.com.vn/i/ecommerce/media/ckeditor_2678325.jpg'),
		('DL.002006',N'Công nghệ làm lạnh trực tiếp giữ thực phẩm tươi ngon',N'Tủ lạnh Aqua AQR AQR-D59FA BS ứng dụng công nghệ làm lạnh trực tiếp, hơi lạnh được thổi lan tỏa đều khắp mọi vị trí trong tủ giúp làm lạnh nhanh, vận hành êm, giữ thực phẩm luôn tươi ngon, hạn chế biến chất. Nhờ thế, tủ tiêu hao rất ít điện khi hoạt động, tiết kiệm chi phí hàng tháng cho người dùng.','https://hc.com.vn/i/ecommerce/media/ckeditor_2678326.png'),
		('00030532',N'Tiết kiệm điện năng hiệu quả',N'Với lượng điện tiêu thụ chỉ vào khoảng 0,55 KW/ngày (0,5 số điện/ngày) giúp sử dụng tiết kiệm điện năng tối ưu cho người dùng, làm giảm nỗi lo chi phí tiền điện hàng tháng cho bạn.','https://hc.com.vn/i/ecommerce/media/ckeditor_1927748.jpg'),
		('00030532',N'Hệ thống làm lạnh trực tiếp',N'Tủ lạnh Electrolux 46 lít EUM0500SA/SB được trang bị hệ thống làm lạnh trực tiếp giúp tủ làm lạnh nhanh hơn, đảm bảo thực phẩm luôn tươi ngon. Ngoài ra, tủ lạnh mini Electrolux vận hành rất êm ái không làm ảnh hưởng đến các sinh hoạt khác của bạn.','https://hc.com.vn/i/ecommerce/media/ckeditor_1927750.jpg'),
		('DL.002387',N'Mặt tủ giả gương sáng bóng, sang trọng, đẹp mắt',N'Tủ lạnh Samsung Inverter 300 lít RT29K5532BU/SV thiết kế đơn giản nhưng vẫn đẹp mắt, tay nắm chìm tinh tế, thanh lịch. Cửa tủ kim loại phủ sơn đen với công nghệ mới cho độ sáng bóng như gương, tăng thêm sự sang trọng cho không gian bếp của gia đình bạn. Tủ lạnh 2 cửa Samsung này được bố trí ngăn đá trên truyền thống phù hợp với thói quen của đa số người dùng. Dung tích sử dụng 300 lít đáp ứng nhu cầu bảo quản và dự trữ thực phẩm cho gia đình 3-4 thành viên.','https://hc.com.vn/i/ecommerce/media/ckeditor_2794012.jpg'),
		('DL.002387',N'2 dàn lạnh độc lập Twin Cooling Plus - Tươi lâu hơn gấp 2 lần',N'Tủ lạnh Samsung Twin Cooling Plus hoạt động với hai hệ thống làm lạnh riêng biệt cho ngăn đá và ngăn mát. Khí lạnh luân chuyển riêng biệt trong từng ngăn, nhờ đó, độ ẩm luôn được bảo toàn ở ngăn mát, giúp thực phẩm tươi ngon lâu hơn gấp 2 lần. Hơn thế nữa, công nghệ hai dàn lạnh độc lập còn giúp hạn chế lẫn mùi giữa thực phẩm tươi sống trong ngăn đông với thực phẩm chín và rau quả trong ngăn mát.','https://hc.com.vn/i/ecommerce/media/ckeditor_2794014.jpg'),
		('DL.004018',N'Thiết kế phẳng sang trọng',N'Tủ lạnh Panasonic Inverter 380 lít NR-BX421WGKV sở hữu thiết kế phẳng cùng với dải bảng điều khiển cảm ứng thanh mảnh bên ngoài mang tới những đường nét gọn gàng và đầy tinh tế, đồng thời cho phép bạn dễ dàng kiểm soát các cài đặt chỉ bằng một cái chạm nhẹ, không cần mở cửa tủ gây thất thoát hơi lạnh.','https://hc.com.vn/i/ecommerce/media/ckeditor_3114614.jpg'),
		('DL.004018',N'Thịt cá tươi ngon đến 7 ngày – Ăn ngon, sống khỏe',N'Thực phẩm tươi sống, đặc biệt là thịt cá luôn ngon nhất khi mua về, tuy nhiên, làm thế nào để giữ được hương vị tươi ngon mà vẫn an toàn cho sức khỏe nếu cần bảo quản lâu hơn? Tính năng cấp đông mềm Prime Fresh với luồng hơi lạnh mang nhiệt độ lí tưởng -3°C giúp thực phẩm được bảo vệ bởi lớp đông nhẹ trên bề mặt, ngăn chặn sự oxi hóa và vi khuẩn xâm nhập vào bên trong mà không bị làm đóng băng, không phá vỡ cấu trúc tế bào. Bảo quản ở trạng thái đông mềm giúp giữ được độ tươi ngon trong khoảng 7 ngày, giảm tối đa sự hao hụt dưỡng chất và giúp chế biến món ăn tiện lợi, dễ dàng mà không cần phải rã đông. Chế độ Prime Fresh+ để thực phẩm đạt trạng thái cấp đông mềm nhanh hơn gấp bốn lần, giảm thời gian biến đổi chất.','https://hc.com.vn/i/ecommerce/media/ckeditor_3114612.jpg'),
